sysauto_script.script:
(function generateRandomNeedItRecords() {
    var numRecords = 1200; // Number of records to create
    var userSysId = 'ENTER_USER_SYS_ID'; // Change to a valid sys_id from your User table (sys_user)
    var locations = ['New York', 'London', 'Toronto', 'San Francisco', 'Sydney']; // Random locations
    var shortDescriptions = ['Upgrade system', 'Implement new feature', 'Fix critical bug', 'Conduct training', 'Deploy new version'];

    for (var i = 0; i < numRecords; i++) {
        var needItRecord = new GlideRecord('x_58872_needit_needit'); // Table name for NeedIt
        needItRecord.initialize();

        // Set random values for fields
        needItRecord.u_requested_for = userSysId; // User who requested it
        needItRecord.u_location = locations[Math.floor(Math.random() * locations.length)];
        needItRecord.u_need_by = gs.daysAgo(-Math.floor(Math.random() * 30)); // Random future date within 30 days
        needItRecord.short_description = shortDescriptions[Math.floor(Math.random() * shortDescriptions.length)];

        // Insert the record
        needItRecord.insert();
    }

    gs.info('Random NeedIt records created successfully!');
})();
